    }
</style>

"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set company_doc = frappe.get_doc("Company", doc.company) %}
{% set business_doc = frappe.get_doc("ZATCA Business Settings", doc.company ~ "-" ~ company_doc.country ~ "-" ~ doc.currency) %}
{% set seller_address = business_doc.street ~ ' ' ~ business_doc.city ~ ' ' ~ business_doc.country %}

<h4 class="text-right py-2">معلومات البائع</h4>
<div class="splitted-container" dir="rtl">
    <div class="section short">
        <h6>اسم البائع</h6>
        <p>{{ business_doc.seller_name }}</p>
    </div>
    <div class="divider"></div>
    <div class="section short">
        <h6>عنوان البائع</h6>
        <p>{{ seller_address }}</p>
    </div>
    <div class="divider"></div>
    <div class="section long">
        <h6>رقم تسجيل ضريبة القيمة المضافة البائع</h6>
        <p>{{ business_doc.vat_registration_number }}</p>
    </div>
    {% if business_doc.other_ids[0] and business_doc.other_ids[0].value %}
    <div class="divider"></div>
    <div class="section short">
        <h6>رقم السجل التجاري</h6>
        <p>
            {{ business_doc.other_ids[0].value }}
        </p>
    </div>
    {% endif %}
</div>

<style>
    .splitted-container {
        display: flex;
        flex-direction: row;
    }

    .section {
        text-align: right;
        padding-right: 2%;
        direction: rtl;
    }

    .short {
        flex-basis: 20%;
    }

    .long {
        flex-basis: 40%
    }

    .divider {
        border-right: 1px solid #ccc;
    }
</style>
