        {% for item in doc.items %}
            <tr>
                <td>{{ item.item_name }}</td>
                <td>{{ item.amount }}</td>
                <td>{{ item.qty }}</td>
                <td>{{ item.custom_tax_total }}</td>
                <td>{{ doc.taxes[0].rate }}</td>
                <td>{{ item.custom_tax_total }}</td>
                <td>{{ item.custom_total_after_tax }}</td>
            </tr>
        {% endfor %}
